import React from 'react';
import {NavLink, Route} from "react-router-dom";
import {connect, useDispatch, useSelector} from "react-redux";
import {logout} from "../../reducers/userReducer";
import {setKind} from "../../reducers/gameReducer";
import saveToLocalStorage, {store} from "../../store";

const SelectGame = (props) => {
    const dispatch = useDispatch();
    const routes = ['history','biology','geography','mixed'];
    // const kind = useSelector(state=>state.game.currentKind)
    // console.log(kind);
    return (

        <div className={"kinds"}>

            {routes.map((item)=>
                <NavLink to={'game/preview/'+item} exact activeClassName={'active'}>
                    {item}
                </NavLink>)}
            {routes.map((item) => (
                <Route key={path} exact path={'game/preview/'+item}>
                {({ match }) => (
                    <CSSTransition
                        in={match != null}
                        timeout={300}
                        classNames="page"
                        unmountOnExit
                    >
                        <div className="page">
                            <Component />
                        </div>
                    </CSSTransition>
                )}
                </Route>
                ))}
                )})


            <NavLink to={'/game/geography'}>
                <button onClick={()=>{ dispatch(setKind("geography"));
                 saveToLocalStorage('game',store.getState().game);
                }}>geography</button>
            </NavLink>
            <NavLink
                to={'/game/history'}>
                <button onClick={()=> {
                    dispatch(setKind("history"));
                    saveToLocalStorage('game',store.getState().game);
                }}>history</button>
            </NavLink>
            <NavLink exact to={'/game/biology'}>
                <button onClick={()=> {
                    dispatch(setKind("biology"));
                    saveToLocalStorage('game',store.getState().game);
                }}>biology</button>
            </NavLink>
            <NavLink to={'/game/mixed'}>
                <button onClick={()=>{ dispatch(setKind("mixed"));
                 saveToLocalStorage('game',store.getState().game);
                }}>Mixed</button>
            </NavLink>
        </div>
    );
};

export default SelectGame;
