import axios from "axios";
import {setUser} from "../reducers/userReducer";
import {startRange} from "./range";
import {startHistory} from "./history";

export const registration = async (email,password) =>{
    try {
        const response = await axios.post('http://localhost:5000/auth/registration',{
            email,
            password
        })
        alert(response.data.message)
    } catch (e) {
        alert(e.response.data.errors.errors[0].msg);

    }
}


export const login = (email,password) =>{
    return async dispatch => {
        try {
            const response = await axios.post('http://localhost:5000/auth/login',{
                email,
                password
            })
            dispatch(setUser(response.data.user));
            localStorage.setItem('token',response.data.token);//локальное хранилище
            dispatch(startHistory());
            dispatch(startRange());
            console.log(response.data);
        } catch (e) {
            console.log(e.response);
            // alert(e.response.data.errors.errors[0].msg);

        }
    }

}

export const auth =  () => {
    return async dispatch => {
        try {
            const response = await axios.get(`http://localhost:5000/auth/auth`,
                {headers:{Authorization:`Bearer ${localStorage.getItem('token')}`}}
            )
            dispatch(setUser(response.data.user))
            localStorage.setItem('token', response.data.token)
        } catch (e) {
            console.log(e.response)
            localStorage.removeItem('token')
        }
    }
}
