import React from "react";
import saveToLocalStorage, {store} from "../store";
import axios from "axios";
import {refreshHistory, setHistory} from "../reducers/appReducer";


export const  startHistory = async () =>{
        try {
            const id = store.getState().user.currentUser.id;
            const response = await axios.post('http://localhost:5000/history/response',{
                id
            });
            counterQuizzes(response.data.response)
            store.dispatch(setHistory(response.data.response));
            saveToLocalStorage('history',response.data.response);

            // console.log(response.data.response)



        } catch (e) {
            console.log(e.response);
        }

        function counterQuizzes(history){
            const userId = store.getState().user.currentUser.id;
            let amount = {
                history:{
                    value:0,
                    avg:0
                },
                biology: {
                    value:0,
                    avg:0
                },
                geography: {
                    value:0,
                    avg:0
                },
                mixed:{
                    value:0,
                    avg:0
                }
            };

            history.forEach((item)=>{
                if (item.userId===userId){
                    switch (item.kind) {
                        case 'history':{
                            amount.history.value++;
                            amount.history.avg += item.result;
                            break;
                        }
                        case 'biology':{
                            amount.biology++;
                            amount.biology.avg += item.result;
                            break;
                        }
                        case 'geography':{
                            amount.geography++;
                            amount.geography.avg += item.result;
                            break;
                        }
                        case 'mixed':{
                            amount.mixed++;
                            amount.mixed.avg += item.result;
                            break;
                        }
                    }
                }
            })
            // Object.keys(amount).map((item)=>{
            //     amount[item].avg /= amount[item].value
            // })
            console.log(amount);

            // saveToLocalStorage('amount',amount);




        }
}



export const actionRefreshHistory = ()=>{
    let history = store.getState().app.history;
    if (history.length === 0) store.dispatch(refreshHistory());
}
