import React from 'react';
import {useDispatch, useSelector} from "react-redux";
import ReactCSSTransitionGroup from "react-addons-css-transition-group";
import {next} from "../../actions/game";

import Answers from "./Answers";
import "../../assets/css/game.css";



const Game = () => {
    const dispatch = useDispatch();
    // dispatch(startGame());
    let input = document.querySelectorAll('input[type=checkbox]');
    input.forEach((item)=>{
        item.className = 'radioCustomButton';
    })
    console.log(input[0]);

    const number = useSelector(state=>state.game.currentQuestionCount);
    const options = useSelector(state=>state.game.currentOptions);
    const flag = useSelector(state=>state.game.endGame);
    const length = useSelector(state=>state.game.questionsLength);
    const rightAnswers = useSelector(state=>state.game.rightAnswersCount);

    return (

        <div>
            {
                !flag ?
                        <ReactCSSTransitionGroup
                            className="container"
                            component="div"
                            transitionName="fade"
                            transitionEnterTimeout={800}
                            transitionLeaveTimeout={500}
                            transitionAppear
                            transitionAppearTimeout={500}>
                            <div>
                                <h3>{number+1} Question of {length}</h3>
                                <h2>{options.question}</h2>
                                <ul className="answerOptions">
                                    {/*Для каждого варианта ответа вызываем функцию*/}
                                    {options.answers.map((item, index) => {
                                        return <Answers type={item.type} content={item.content} />
                                    })}
                                </ul>
                                <button type={"button"} onClick={()=>next(number + 1,length)}>Check</button>
                            </div>
                        </ReactCSSTransitionGroup>
                :
                <div>{rightAnswers} of {length}</div>
            }
        </div>

    );

};


export default Game;
