import React from 'react';
import {useDispatch, useSelector} from "react-redux";
import View from "./View";
import {nextGame, startGame} from "../../reducers/gameReducer";
const questions = require('../../assets/questions.json');
const Game = () => {
    const dispatch = useDispatch();
    // dispatch(startGame());
    const number = useSelector(state=>state.game.currentQuestionCount);
    const options = useSelector(state=>state.game.currentOptions);
    const endGame = useSelector(state=>state.game.endGame);
    return (

        <div>
            {
                !endGame ?
                <div>
                    <h3>{number} Question</h3>
                    <h2>{options.question}</h2>
                    <ul>
                    {
                        options.answers.map((item, index) => {
                            return <li key={index}>{item.content}</li>
                        })
                    }
                    </ul>
                    <button type={"button"} onClick={nextGame(number + 1)}>Check</button>
                </div>
                :
                <div>Game over!</div>
            }
        </div>

    );

};


export default Game;
