import React from "react";
import saveToLocalStorage, {loadFromLocalStorage, store} from "../store";
import axios from "axios";
import {refreshRange, setRange} from "../reducers/appReducer";



export const  startRange = () =>{
    function sort(arr) {
        return arr.sort((a,b)=>{
            return b.result - a.result;
        })
    }

    let historyArr = [],biologyArr=[],geographyArr=[],mixedArr = [];
    // try {
    //     // await delay(1000);
    //     const response =  await axios.post('http://localhost:5000/history/range',{
    //         kind
    //     });
    //     // document.getElementById('preloader').hidden = true;
    //     return store.dispatch(setRange(response.data.response));
    //
    // } catch (e) {
    //     console.log(e.response);
    // }
    let history = store.getState().app.history;
    history.forEach((item)=>{
        switch (item.kind) {
            case 'history':
                historyArr.push(item)
                break;
            case 'biology':
                biologyArr.push(item)
                break;
            case 'geography':
                geographyArr.push(item)
                break;
            case 'mixed':
                mixedArr.push(item)
                break;
            default:
                break;

        }
    });

    let range = {
        history: sort(historyArr),
        biology: sort(biologyArr),
        geography:sort(geographyArr),
        mixed:sort(mixedArr)
    }

    console.log(range);

    store.dispatch(setRange(range,top(range)));
    saveToLocalStorage('range',range);
    saveToLocalStorage('top',top(range));


}

function top(range) {
    const userId = store.getState().user.currentUser.id;

    let top =  {
        history:0,
        biology: 0,
        geography: 0,
        mixed: 0
    }

    Object.keys(top).map((item)=>{
        writeTop(item);
    })

    function writeTop(kind){
        range[kind].every((item,number)=>{
            if (item.userId === userId){
                top[kind] = number+1
                return false;
            }
        })
    }

    return top;


}

export const actionRefreshRange = ()=>{
    const range = store.getState().app.range;
    const localRange = loadFromLocalStorage('range');
    if (Object.keys(range).length === 0 && Object.keys(localRange).length !== 0) store.dispatch(refreshRange());
}
