import React, {useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {CSSTransition} from "react-transition-group";
import {next, setAnswer, startGame} from "../../actions/game";
import {store} from "../../store";
import Answers from "./Answers";
import Result from "./Result";
import "../../assets/css/game.css";


window.onbeforeunload = function (event) {
  return false;
}




class Game extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            loaderVisible:true
        }
        this.clear = this.clear.bind(this);
        this.shuffleArray = this.shuffleArray.bind(this);
        this.setLoaderVisible = this.setLoaderVisible.bind(this);

    }
    componentDidMount() {
        this.setLoaderVisible(true);
    }

    componentWillUnmount() {
        return alert('Are you sure?');
    }
    setLoaderVisible(loader){
        this.setState({
            loaderVisible:loader
        })
    }
    shuffleArray(array) {
        var currentIndex = array.length, temporaryValue, randomIndex;
        // Пока остаются элементы, чтобы перемешать …
        while (0 !== currentIndex) {
            // Выберите оставшийся элемент …
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex -= 1;
            // И поменяйте местами с текущим элементом.
            temporaryValue = array[currentIndex];
            array[currentIndex] = array[randomIndex];
            array[randomIndex] = temporaryValue;
        }
        return array;
    }

    clear(){
        document.getElementById('next').style.display = 'none';
        document.getElementById('check').style.display = 'block';
        document.querySelectorAll('input[type=checkbox]').forEach((item)=>{
            item.disabled = false;
        });
    }


    render() {

        startGame();
        // const [loaderVisible,setLoaderVisible] = useState(false);

        let number = store.getState().game.currentQuestionCount;
        let options = store.getState().game.currentOptions;
        let flag = store.getState().game.endGame;
        let length = store.getState().game.questionsLength;
        let rightAnswers = store.getState().game.rightAnswersCount;
        let kind = store.getState().game.currentKind;


        let answers = this.shuffleArray(options.answers);

        console.log(this.state.loaderVisible);
        return (

            <div>
                <h2>{kind}</h2>
                {
                    !flag ?

                        <CSSTransition
                            key={number}
                            in={this.state.loaderVisible}
                            timeout={500}
                            classNames={"answers"}
                        >
                            <div className="answers" key={number}>
                                <h3>{number + 1} Question of {length}</h3>
                                <h2>{options.question}</h2>
                                <ul className="answerOptions">
                                    {/*Для каждого варианта ответа вызываем функцию*/}
                                    {answers.map((item, index) => {
                                        return <Answers key={index} type={item.type} content={item.content}/>
                                    })}
                                </ul>
                                <div style={{display: 'flex', flexDirection: 'row'}}>
                                    <button type={"button"} id={'check'} onClick={() => setAnswer()}>Check</button>
                                    <button type={"button"} style={{display: 'none'}} id={'next'} onClick={
                                        () => { next(number + 1, length);
                                        if (length - number !== 1) this.setLoaderVisible(!this.state.loaderVisible);
                                        this.clear();
                                    }}>{length - number === 1 ? "Finish" : "Next"}</button>
                                </div>
                            </div>
                        </CSSTransition>
                        :
                        <Result number={number} answers={rightAnswers} length={length}/>

                }
            </div>

        );
    }

}


export default Game;
