import React, {useState} from 'react';
import {useSelector} from "react-redux";
import {useInput} from "../../utils/validator/validator";
import Input from "../../utils/input/input";
import {edit} from "../../actions/settings";

const Settings = () => {
    const email = useSelector(state=>state.user.currentUser.email)
    const oldPassword = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength: {value:3,text:'min length'},maxLength: {value:8,text:'max length'}})
    const newPassword = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength: {value:3,text:'min length'},maxLength: {value:8,text:'max length'}})
    const  [form,setForm] = useState(false);
    return (
        <div>
            <img src="" alt=""/>
            <div className={'d-flex flex-column'}>
                <div>
                    Email: {email}
                </div>
                <div className={'d-flex flex-column'}>
                    <h3>History of the quizzes</h3>
                    <div className={'d-flex'}>
                        <span>
                            History: <br/> 2
                        </span>
                        <span>
                            Biology: <br/> 2
                        </span>
                        <span>
                            Geography: <br/> 2
                        </span>
                    </div>
                </div>
                <div className={'d-flex flex-column'}>
                    <h3>Top at follow kind</h3>
                    <div className={'d-flex'}>
                        <span>
                            History: <br/> 2
                        </span>
                        <span>
                            Biology: <br/> 2
                        </span>
                        <span>
                            Geography: <br/> 2
                        </span>
                    </div>
                </div>
            </div>
            <button onClick={setForm(!form)}>Edit profile</button>

            <div>
                {oldPassword.isDirty && oldPassword.printError(['isEmpty','minLengthError','maxLengthError'])}

                <Input setValue={oldPassword.onChange} onBlur={oldPassword.onBlur} value={oldPassword.value}  type={"password"} placeholder={"input password..."}/>

                {newPassword.isDirty && newPassword.printError(['isEmpty','minLengthError','maxLengthError'])}

                <Input setValue={newPassword.onChange} onBlur={newPassword.onBlur} value={newPassword.value}  type={"password"} placeholder={"input password..."}/>
                {/*{(!email.inputValid.value || !password.inputValid.value) && <div>{email.inputValid.text || password.inputValid.text}</div>}*/}

                <button
                        disabled={!oldPassword.inputValid.value || !newPassword.inputValid.value}
                        onClick={()=> edit(oldPassword.value,newPassword.value)  }>Save</button>
            </div>

        </div>
    );
};

export default Settings;
