import React from "react";
import saveToLocalStorage, {store} from "../store";
import axios from "axios";
import {refreshRange, setRange} from "../reducers/appReducer";



export const  startRange = () =>{
    console.log('happy')
    function sort(arr) {
        return arr.sort((a,b)=>{
            return b.result - a.result;
        })
    }

    let historyArr = [],biologyArr=[],geographyArr=[],mixedArr = [];
    // try {
    //     // await delay(1000);
    //     const response =  await axios.post('http://localhost:5000/history/range',{
    //         kind
    //     });
    //     // document.getElementById('preloader').hidden = true;
    //     return store.dispatch(setRange(response.data.response));
    //
    // } catch (e) {
    //     console.log(e.response);
    // }
    let history = store.getState().app.history;
    console.log(history);
    history.forEach((item)=>{
        switch (item.kind) {
            case 'history':
                historyArr.push(item)
                break;
            case 'biology':
                biologyArr.push(item)
                break;
            case 'geography':
                geographyArr.push(item)
                break;
            case 'mixed':
                mixedArr.push(item)
                break;
            default:
                break;

        }
    });

    let range = {
        history: sort(historyArr),
        biology: sort(biologyArr),
        geography:sort(geographyArr),
        mixed:sort(mixedArr)
    }

    store.dispatch(setRange(range));
    saveToLocalStorage('range',range);


}

function top(range) {
    const userId = store.getState().user.id;
    let top =  {
        history:0,
        biology: 0,
        geography: 0,
        mixed: 0
    }

    range.history.forEach((item)=>{
        if (item.userId === userId){
            switch (item.kind) {
                case 'history':
                    top.history = item.result
                    break;
                case 'biology':
                    top.biology++
                    break;
                case 'geography':
                    top.geography++
                    break;
                case 'mixed':
                    top.mixed++
                    break;
            }
        }
    })

}

export const refresh = ()=>{
    let range = store.getState().app.range;
    if (Object.keys(range).length === 0) store.dispatch(refreshRange());
}
