const questions = require('../assets/questions.json');

const SET_KIND="SET_KIND";
const START_GAME="START_GAME";
const NEXT_GAME="NEXT_GAME";


const defaultState = {
    currentKind:"",
    currentQuestionCount:0,
    questionContent:'',
    currentAnswersOption:[],
    answer:[],
    currentOptions:{},
    questionsLength: 0
}

export default function gameReducer(state=defaultState,action) {
    switch (action.type) {
        case SET_KIND:
            const question = questions[action.payload][0];
            return {
                ...state,
                currentKind: action.payload,
                currentOptions: question,
                questionsLength: questions[action.payload].length
                // questionContent: question.question,
                // currentAnswersOption: question.answers,
                // answer: question.answer
            }
        // case START_GAME:
        //
        //     return {
        //         ...state,
        //         currentOptions: question,
        //         questionContent: question.question,
        //         currentAnswersOption: question.answers,
        //         answer: question.answer
        //     }
        case NEXT_GAME:{
            const question = questions[state.currentKind][state.currentQuestionCount+1];
            return {
                ...state,
                currentQuestionCount: state.currentQuestionCount+1,
                // questionContent: question.question,
                // currentAnswersOption: question.answers,
                currentOptions: question
            }

        }
        default:
            return state;
    }
}

export const setKind = (name) =>({type:SET_KIND,payload:name})
export const startGame = () =>({type:START_GAME})
export const nextGame = () => ({type:NEXT_GAME})
