import React from "react";
import {endGame, nextGame} from "../reducers/gameReducer";
import {store} from "../reducers";
import {useSelector} from "react-redux";


export const start = () =>{

}

export const next = (current,length) =>{

    var answer = checkAnswer();
    console.log(answer);
    if (current === length) {
        setTimeout(() => end(answer), 500);

    } else  {
        setTimeout(() => store.dispatch(nextGame(answer)), 500);
    }
}


const end = (answer) =>{
    store.dispatch(endGame(answer));
}

const giveAnswers = () =>{
    var selectedCheckBoxes = document.querySelectorAll('input:checked');
    return Array.from(selectedCheckBoxes).map(cb => cb.value);
}

export const setAnswer = ()=>{
    let flag = checkAnswer();
    let answer = giveAnswers();

    answer.map((item,index)=>{
        let input = document.getElementById(item);
        input.className = flag ? "radioCustomButton1" : "radioCustomButton2";
    });
}

const checkAnswer = () =>{
    let answer = giveAnswers();
    console.log(answer);
    let count = 0, flag = false,
        rightAnswer = store.getState().game.rightAnswer;
    answer.forEach((item)=>{
        if(rightAnswer.includes(item))
            count++;
    });
    if(rightAnswer.length === count && answer.length === rightAnswer.length){
        flag = true;
    }
    return flag;
}
