import React from "react";
import {store} from "../reducers";
import axios from "axios";
import {setRange} from "../reducers/appReducer";

const delay = ms =>{
    return new Promise(resolve => {
        setTimeout(()=>{
            resolve()
        },ms)
    })
}

export const  start = async (kind) =>{
    try {
        // await delay(1000);
        const response =  await axios.post('http://localhost:5000/history/range',{
            kind
        });
        document.getElementById('preloader').hidden = false;
        return store.dispatch(setRange(response.data.response));

    } catch (e) {
        console.log(e.response);
    }

}
