import React, {useEffect, useState} from 'react';
import "./registration.css";
import Input from "../../utils/input/input";
import {useDispatch} from "react-redux";
import {login} from "../../actions/user";

const useValidation = (value,validations)=>{
    const [isEmpty,setEmpty] = useState({value:true,text:''})
    const [minLengthError,setMinLengthError] = useState(false)
    const [maxLengthError,setMaxLengthError] = useState(false)
    const [emailError,setEmailError] = useState(false)
    const [inputValid,setInputValid] = useState(false)


    useEffect(()=>{
        for (const validation in validations) {
            switch (validation) {
                case 'minLength':
                    value.length < validations[validation]? setMinLengthError(true) : setMinLengthError(false)
                    break;
                case 'isEmpty':
                    value?setEmpty({value: false,text: ''}): setEmpty({value: false,text: validations[validation].text})
                    break;
                case'maxLength':
                    value.length > validations[validation]? setMaxLengthError(true) : setMaxLengthError(false)
                    break;
                case 'isEmail':
                    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                     re.test(String(value ).toLowerCase()) ? setEmailError(false) : setEmailError(true)
                    break;

            }
        }
    },[value])

    useEffect(()=>{
        if(isEmpty || maxLengthError || minLengthError || emailError){
            setInputValid(false)
        } else {
            setInputValid(true)
        }
    },[isEmpty,maxLengthError,minLengthError,emailError])

    return {isEmpty,minLengthError,emailError,maxLengthError}
}

const useInput = (initialState,validations) =>{
    const [value,setValue] = useState(initialState);
    const [isDirty,setDirty] = useState(false)
    const valid = useValidation(value,validations)


    const onChange = (e) =>{
        setValue(e.target.value)
    }

    const onBlur = (e)=>{
     setDirty(true)
    }

    return{
        value,onChange,onBlur,isDirty,...valid
    }
}

const Login = () => {
    const email= useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength:3,isEmail:true})
    const password = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength:5,maxLength:8})
    const dispatch = useDispatch();
    return (
        <div className={'registration'}>
            <div className="registration_header">Authorization</div>
            {(email.isDirty && email.isEmpty) && <div style={{color:'red'}}>{email.isEmpty.text}</div>}
            {(email.isDirty && email.minLengthError) && <div style={{color:'red'}}>{"min length"}</div>}
            {(email.isDirty && email.emailError) && <div style={{color:'red'}}>{"emailError"}</div>}
            <Input setValue={email.onChange} onBlur={email.onBlur} value={email.value}  type={"text"} placeholder={"input email..."}/>
            {(password.isDirty && password.isEmpty) && <div style={{color:'red'}}>{password.isEmpty.text}</div>}
            {(password.isDirty && password.minLengthError) && <div style={{color:'red'}}>{"min length"}</div>}
            {(password.isDirty && password.maxLengthError) && <div style={{color:'red'}}>{"max length"}</div>}
            <Input setValue={password.onChange} onBlur={password.onBlur} value={password.value}  type={"password"} placeholder={"input password..."}/>
            <button className="registration_btn" onClick={()=>dispatch(login(email,password))}>Log in</button>
        </div>
    );
};

export default Login;
