import React from "react";
import {endGame, nextGame} from "../reducers/gameReducer";
import {store} from "../reducers";
import {useSelector} from "react-redux";


export const start = () =>{

}

export const next = (current,length) =>{
    var selectedCheckBoxes = document.querySelectorAll('input.radioCustomButton:checked');
    var checkedValues = Array.from(selectedCheckBoxes).map(cb => cb.value);
    var answer = setAnswer(checkedValues);
    if (current === length) {
        end();

    } else  {
        setTimeout(() => store.dispatch(nextGame(answer)), 500);
        checkedValues.map((item)=>{
            let input = document.getElementById(item);
            input.className = "radioCustomButton";
        })
    }
}





const end = () =>{
    store.dispatch(endGame())
}

const setAnswer = (answer) =>{
    let count = 0, flag = false,
        rightAnswer = store.getState().game.rightAnswer;
    console.log(rightAnswer);
    answer.map((item,index)=>{
        if(rightAnswer.includes(item))
            count++;
    });
    if(rightAnswer.length === count){
        flag = true;
    }
    answer.map((item,index)=>{
        let input = document.getElementById(item);
        input.className = flag ? "radioCustomButton1" : "radioCustomButton2";
    });
    return flag;
}
