import React from 'react';
import {NavLink} from "react-router-dom";
import {connect, useDispatch, useSelector} from "react-redux";
import {logout} from "../../reducers/userReducer";
import {setKind} from "../../reducers/gameReducer";
import saveToLocalStorage, {store} from "../../store";

const SelectGame = (props) => {
    const dispatch = useDispatch();
    // const kind = useSelector(state=>state.game.currentKind)
    // console.log(kind);
    return (
        <div className={"kinds"}>
            <NavLink to={'/game/geography'}>
                <button onClick={()=> dispatch(setKind("geography"))}>geography</button>
            </NavLink>
            <NavLink
                to={'/game/history'}>
                <button onClick={()=> {
                    dispatch(setKind("history"));
                    saveToLocalStorage('game',store.getState().game);
                }}>history</button>
            </NavLink>
            <NavLink exact to={'/game/biology'}>
                <button onClick={()=> dispatch(setKind("biology"))}>biology</button>
            </NavLink>
            <NavLink to={'/game/mixed'}>
                <button onClick={()=> dispatch(setKind("mixed"))}>Mixed</button>
            </NavLink>
        </div>
    );
};

export default SelectGame;
