import React, {Suspense, useEffect, useState} from 'react';
import {useSelector} from "react-redux";

import {start} from "../../actions/range";
import Modal from "../../utils/Modal/Modal";

const Range = () => {


    let range = useSelector(state=>state.app.range.history);
    // range = range.sort((a,b)=>{
    // return b.result - a.result;
    // });

    // useEffect(()=>{
    //     range = range.history
    // },[Range])

    function changeRange(kind) {
        range = range[kind];
    }

    return (
        <div>

            {/*<div  className="d-flex justify-content-center">*/}
            {/*    <div id={"preloader"}  className="spinner-border" role="status">*/}
            {/*        <span className="sr-only">Loading...</span>*/}
            {/*    </div>*/}
            {/*</div>*/}

            <nav className="navbar navbar-light bg-light">
                <div className="container-fluid justify-content-start">
                    <button  className= {range.length === 0 ? "btn btn-outline-success active" : "btn btn-outline-success "}
                            onClick={()=>changeRange('history')}
                            type="button">History</button>
                    <button className="btn btn-outline-success"
                            onClick={()=>changeRange('geography')}
                            type="button">Geography</button>
                    <button className="btn btn-outline-success"
                            onClick={()=>changeRange('biology')}
                            type="button">Biology</button>
                    <button className="btn btn-outline-secondary"
                            onClick={()=>changeRange('mixed')}
                            type="button">Mixed quiz</button>
                </div>
                <div>
                    <table>
                        <thead className={"text-center border border-secondary"}>
                            <th>â„–</th>
                            <th>Username</th>
                            <th>Percentage of answers is right </th>
                            <th>Date of this quiz</th>
                        </thead>

                        <tbody>
                        {
                            range.map((item,index)=>{
                                return (
                                    <tr key={index} className={"text-center border border-secondary"}>
                                        <td>{index+1}</td>
                                        <td>{item.username}</td>
                                        <td>{item.result}%</td>
                                        <td>{item.date}</td>
                                    </tr>
                                )
                            })
                        }
                        </tbody>
                    </table>
                </div>
            </nav>
        </div>
    );
};

export default Range;
