import React, {useEffect, useState} from 'react';
import "./registration.css";
import Input from "../../utils/input/input";
import {useDispatch} from "react-redux";
import {login} from "../../actions/user";

const useValidation = (value,validations)=>{
    const [isEmpty,setEmpty] = useState({value:true,text:''})
    const [minLengthError,setMinLengthError] = useState({value:false,text:''})
    const [maxLengthError,setMaxLengthError] = useState({value:false,text:''})
    const [emailError,setEmailError] = useState({value:false,text:''})
    const [inputValid,setInputValid] = useState({value:false,text:''})


    useEffect(()=>{
        for (const validation in validations) {
            switch (validation) {
                case 'minLength':
                    value.length < validations[validation].value? setMinLengthError({value: true,text: validations[validation].text}) : setMinLengthError({value: false,text: ''})
                    break;
                case 'isEmpty':
                    value?setEmpty({value: false,text: ''}): setEmpty({value: true,text: validations[validation].text})
                    break;
                case'maxLength':
                    value.length > validations[validation].value? setMaxLengthError({value: true,text: validations[validation].text}) : setMaxLengthError({value: false,text: ''})
                    break;
                case 'isEmail':
                    const re = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;
                     re.test(String(value ).toLowerCase()) ? setEmailError({value: false,text: ''}) : setEmailError({value: true,text: validations[validation].text})
                    break;

            }
        }
    },[value])

    useEffect(()=>{
        if(isEmpty.value || maxLengthError.value || minLengthError.value || emailError.value){
            setInputValid({value: false,text: ''})
        } else {
            setInputValid({value: true,text: 'Form is invalid'})
        }
    },[isEmpty.value,maxLengthError.value,minLengthError.value,emailError.value])

    return {isEmpty,minLengthError,emailError,maxLengthError,inputValid}
}

const useInput = (initialState,validations) =>{
    const [value,setValue] = useState(initialState);
    const [isDirty,setDirty] = useState(false)
    const valid = useValidation(value,validations)


    const onChange = (e) =>{
        setValue(e.target.value)
    }

    const onBlur = (e)=>{
     setDirty(true)
    }

    const printError = ()=>{
        let flag = false;
        for (let i = 0; i<validations.length;i++){
           for (let j = 0; j<i;j++){
              if (j<i){
                  flag = !validations[j].value;
              }
              else {
                  flag = validations[i].value;
              }
           }
            if (flag) return validations[i].text;
        }

    }

    return{
        value,onChange,onBlur,isDirty,...valid
    }
}

const Login = () => {
    const email= useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength:{value:3,text:"min length"},isEmail: {value:true,text:'email error'}})
    const password = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength: {value:5,text:'min length'},maxLength: {value:8,text:'max length'}})
    const dispatch = useDispatch();

    return (
        <div className={'registration'}>
            <div className="registration_header">Authorization</div>
            {(email.isDirty && email.isEmpty.value) && <div style={{color:'red'}}>{email.isEmpty.text}</div>}
            {(email.isDirty && !email.isEmpty.value && email.minLengthError.value) && <div style={{color:'red'}}>{email.minLengthError.text}</div>}
            {(email.isDirty && !email.isEmpty.value && !email.minLengthError.value && email.emailError.value) && <div style={{color:'red'}}>{email.emailError.text}</div>}
            <Input setValue={email.onChange} onBlur={email.onBlur} value={email.value}  type={"text"} placeholder={"input email..."}/>
            {(password.isDirty && password.isEmpty.value) && <div style={{color:'red'}}>{password.isEmpty.text}</div>}
            {(password.isDirty && !password.isEmpty.value && password.minLengthError.value) && <div style={{color:'red'}}>{password.minLengthError.text}</div>}
            {(password.isDirty && !password.isEmpty.value && !password.minLengthError.value && password.maxLengthError.value) && <div style={{color:'red'}}>{password.maxLengthError.text}</div>}
            <Input setValue={password.onChange} onBlur={password.onBlur} value={password.value}  type={"password"} placeholder={"input password..."}/>
            {email.isDirty && password.isDirty && (!email.inputValid.value || !password.inputValid.value) && <div>{email.inputValid.text}</div>}
            <button disabled={!email.inputValid.value || !password.inputValid.value} className="registration_btn" onClick={()=>dispatch(login(email,password))}>Log in</button>
        </div>
    );
};

export default Login;
