import React, {useState} from 'react';
import "./registration.css";
import Input from "../../utils/input/input";
import {useDispatch} from "react-redux";
import {login} from "../../actions/user";


const useInput = (initialState) =>{
    const [value,setValue] = useState(initialState);
    const [isDirty,setDirty] = useState(false)
    const onChange = (e) =>{
        setValue(e.target.value)
    }

    const onBlur = (e)=>{
     setDirty(true)
    }

    return{
        value,onChange,onBlur
    }
}

const Login = () => {
    const email= useInput('')
    const password = useInput('')
    const dispatch = useDispatch();
    return (
        <div className={'registration'}>
            <div className="registration_header">Authorization</div>
            <Input onChange={e=> email.onChange(e)} onBlur={e=> email.onBlur(e)} value={email.value}  type={"text"} placeholder={"input email..."}/>
            <Input onChange={e=> password.onChange(e)} onBlur={e=> password.onBlur(e)} value={password.value}  type={"password"} placeholder={"input password..."}/>
            <button className="registration_btn" onClick={()=>dispatch(login(email,password))}>Log in</button>
        </div>
    );
};

export default Login;
