import {useDispatch, useSelector} from "react-redux";
import {setUser} from "../reducers/userReducer";
import {endGame, nextGame} from "../reducers/gameReducer";
const length = useSelector(state=>state.game.questionsLength);
const dispatch = useDispatch();
export const next = (current) =>{
    console.log(current,length);
    if (current === length) {
        end();
    } else dispatch(nextGame())
}

export const end = () =>{
    dispatch(endGame());
}
