import React, {useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {Transition, CSSTransition, SwitchTransition, TransitionGroup} from "react-transition-group";
import {next, setAnswer, startGame} from "../../actions/game";

import Answers from "./Answers";
import Result from "./Result";
import "../../assets/css/game.css";
import {refreshGame} from "../../reducers/gameReducer";



const Game = () => {
    const dispatch = useDispatch();
    //  dispatch(startGame());
     startGame();
    const [loaderVisible,setLoaderVisible] = useState(false);
    let number = useSelector(state=>state.game.currentQuestionCount);
    let options = useSelector(state=>state.game.currentOptions);
    let flag = useSelector(state=>state.game.endGame);
    let length = useSelector(state=>state.game.questionsLength);
    let rightAnswers = useSelector(state=>state.game.rightAnswer);
    let kind = useSelector(state=>state.game.currentKind);
    if (!kind) {
        dispatch(refreshGame());
    }

    let answers = shuffleArray(options.answers);

    setTimeout(()=>setLoaderVisible(true),);


    function shuffleArray(array) {
        var currentIndex = array.length, temporaryValue, randomIndex;
        // Пока остаются элементы, чтобы перемешать …
        while (0 !== currentIndex) {
            // Выберите оставшийся элемент …
            randomIndex = Math.floor(Math.random() * currentIndex);
            currentIndex -= 1;
            // И поменяйте местами с текущим элементом.
            temporaryValue = array[currentIndex];
            array[currentIndex] = array[randomIndex];
            array[randomIndex] = temporaryValue;
        }
        return array;
    }

    function clear(){
        document.getElementById('next').style.display = 'none';
        document.getElementById('check').style.display = 'block';
        document.querySelectorAll('input[type=checkbox]').forEach((item)=>{
            item.disabled = false;
        });
    }

    return (

        <div>
            <h2>{kind}</h2>
            {
                !flag ?

                    <CSSTransition
                        key={number}
                        in={loaderVisible}
                        timeout={500}
                        classNames={"answers"}
                        // mountOnEnter
                        // unmountOnExit
                    >
                            <div className="answers" key={number}>
                                <h3>{number+1} Question of {length}</h3>
                                <h2>{options.question}</h2>
                                <ul className="answerOptions">
                                    {/*Для каждого варианта ответа вызываем функцию*/}
                                    {answers.map((item, index) => {
                                        return <Answers type={item.type} content={item.content} />
                                    })}
                                </ul>
                                <div style={{display:'flex',flexDirection:'row'}}>
                                    <button type={"button"} id={'check'} onClick={()=>setAnswer()}>Check</button>
                                    <button type={"button"} style={{display:'none'}} id={'next'} onClick={()=>{
                                        next(number + 1,length);
                                        if (length-number!==1) setLoaderVisible(!loaderVisible);
                                        clear();
                                    }}>{length-number===1?"Finish":"Next"}</button>
                                </div>
                            </div>
                    </CSSTransition>
                :
                    <Result number={number} answers={rightAnswers} length={length}/>

            }
        </div>

    );


};


export default Game;
