import React from 'react';
import {useDispatch, useSelector} from "react-redux";
import View from "./View";
import {Next} from "../../actions/game";
import {endGame, nextGame} from "../../reducers/gameReducer";
const questions = require('../../assets/questions.json');
const Game = () => {
    const dispatch = useDispatch();
    // dispatch(startGame());
    const number = useSelector(state=>state.game.currentQuestionCount);
    const options = useSelector(state=>state.game.currentOptions);
    const endGame = useSelector(state=>state.game.endGame);
    const length = useSelector(state=>state.game.questionsLength);
    const next = (current,length) =>{
        console.log(current,length);
        if (current === length) {
            dispatch(endGame())
        } else  dispatch(nextGame())
    }
    return (

        <div>
            {
                !endGame ?
                <div>
                    <h3>{number} Question</h3>
                    <h2>{options.question}</h2>
                    <ul>
                    {
                        options.answers.map((item, index) => {
                            return <li key={index}>{item.content}</li>
                        })
                    }
                    </ul>
                    <button type={"button"} onClick={next(number + 1,length)}>Check</button>
                </div>
                :
                <div>Game over!</div>
            }
        </div>

    );

};


export default Game;
