const SET_HISTORY="SET_HISTORY";
const SET_RANGE="SET_RANGE";
const SET_CURRENTLY_RANGE="SET_CURRENTLY_RANGE";

const defaultState = {
    history: [],
    range: {},
    currentlyRange:[],
}

export default function appReducer(state=defaultState,action) {
    switch (action.type) {
        case SET_HISTORY:
            return {
                ...state,
                history: action.payload,
            }
        case SET_RANGE:
            return {
                ...state,
                range: action.payload
            }
        case SET_CURRENTLY_RANGE:
            return {
                ...state,
                currentlyRange: state.range[action.payload]
            }

        default:
            return state;
    }
}

export const setHistory = (history) =>({type:SET_HISTORY,payload:history})
export const setRange = (range) =>({type:SET_RANGE,payload:range})
export const setCurrentlyRange = (kind) =>({type:SET_CURRENTLY_RANGE,payload:kind})
