import React, {useEffect, useState} from 'react';
import "./registration.css";
import Input from "../../utils/input/input";
import {useDispatch} from "react-redux";
import {login} from "../../actions/user";
import {useInput} from "../../utils/validator/validator";

const Login = () => {
    const email= useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength:{value:3,text:"min length"},isEmail: {value:true,text:'email error'}})
    const password = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength: {value:5,text:'min length'},maxLength: {value:8,text:'max length'}})
    const dispatch = useDispatch();

    return (
        <div className={'registration'}>
            <div className="registration_header">Authorization</div>

            {email.isDirty && email.printError(['isEmpty','minLengthError','emailError'])}

            <Input setValue={email.onChange} onBlur={email.onBlur} value={email.value}  type={"text"} placeholder={"input email..."}/>

            {password.isDirty && password.printError(['isEmpty','minLengthError','maxLengthError'])}

            <Input setValue={password.onChange} onBlur={password.onBlur} value={password.value}  type={"password"} placeholder={"input password..."}/>
            {(!email.inputValid.value || !password.inputValid.value) && <div>{email.inputValid.text || password.inputValid.text}</div>}
            <button disabled={!email.inputValid.value || !password.inputValid.value}
                    className="registration_btn"
                    onClick={()=>dispatch(login(email,password))}>Log in</button>
        </div>
    );
};

export default Login;
