import React, {useEffect,Suspense, lazy} from "react";
import Navbar from "./navbar/Navbar";
import {BrowserRouter,Route,Switch} from "react-router-dom";
import "./app.css";
import Registration from "./registration/Registration";
import Login from "./registration/Login";
import {useDispatch, useSelector} from "react-redux";
import {auth} from "../actions/user";
import SelectGame from "./game/SelectGame";
import Game from "./game/Game.jsx";
import History from "./history/History";
import Range from "./range/Range";
import {start} from "../actions/range";
import {startGame} from "../actions/game";
import {store} from "../store";
import Settings from "./settings/Settings";
import {startHistory} from "../actions/history";


function App() {
    const isAuth = useSelector(state=>state.user.isAuth)
    const isGame = useSelector(state=>state.game.startGame)
    const history = useSelector(state=>state.app.history)
    const dispatch = useDispatch();


    useEffect(()=>{
        dispatch(auth());
    },[])


    return (
        <BrowserRouter>
            {!isGame ?<div className={"app"}>
                <Navbar />
            </div>
            : ''}
            {!isAuth ?
                <Switch>
                    <Route path={"/registration"} component={Registration}/>
                    <Route path={"/login"} component={Login}/>
                </Switch> :
                <Suspense fallback={<div>Loading...</div>}>
                <Switch>
                    <Route exact path={"/game"} component={SelectGame}/>
                    <Route path={"/game/:name"} component={Game}/>
                    <Route {...startHistory()} exact path={"/history"} component={History}/>
                    <Route {...start(history)} exact path={"/range"} component={Range} />
                    <Route exact path={"/settings"} component={Settings}/>
                </Switch>
                </Suspense>
            }

        </BrowserRouter>
);
}

export default App;
