import React, {Suspense, useEffect, useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import {loadFromLocalStorage, store} from "../../store";
import Modal from "../../utils/Modal/Modal";
import {setCurrentlyRange} from "../../reducers/appReducer";
import {actionRefreshRange} from "../../actions/range";

const Range = () => {

    const dispatch = useDispatch();
    let range = useSelector(state=>state.app.currentlyRange);
    const localHistory = loadFromLocalStorage('history');
    return (
        <div>
            {localHistory.length === 0 ?
                <div>
                    You don't play in quiz yet
                </div>
                :

                /*<div  className="d-flex justify-content-center">*/
                /*    <div id={"preloader"}  className="spinner-border" role="status">*/
                /*        <span className="sr-only">Loading...</span>*/
                /*    </div>*/
                /*</div>*/

                <nav className="navbar navbar-light bg-light">
                    <div className="container-fluid justify-content-start">
                        <button
                            className={range.length === 0 ? "btn btn-outline-success active" : "btn btn-outline-success "}
                            onClick={() => dispatch(setCurrentlyRange('history'))}
                            type="button">History
                        </button>
                        <button className="btn btn-outline-success"
                                onClick={() => dispatch(setCurrentlyRange('geography'))}
                                type="button">Geography
                        </button>
                        <button className="btn btn-outline-success"
                                onClick={() => dispatch(setCurrentlyRange('biology'))}
                                type="button">Biology
                        </button>
                        <button className="btn btn-outline-secondary"
                                onClick={() => dispatch(setCurrentlyRange('mixed'))}
                                type="button">Mixed quiz
                        </button>
                    </div>
                    <div>
                        <table>
                            <thead className={"text-center border border-secondary"}>
                            <th>â„–</th>
                            <th>Username</th>
                            <th>Percentage of answers is right</th>
                            <th>Date of this quiz</th>
                            </thead>

                            <tbody>
                            {
                                range.map((item, index) => {
                                    return (
                                        <tr key={index} className={"text-center border border-secondary"}>
                                            <td>{index + 1}</td>
                                            <td>{item.username}</td>
                                            <td>{item.result}%</td>
                                            <td>{item.date}</td>
                                        </tr>
                                    )
                                })
                            }
                            </tbody>
                        </table>
                    </div>
                </nav>
            }
        </div>
    );
};

export default Range;
