import axios from "axios";
import {setUser} from "../reducers/userReducer";


export const edit = (email,oldPassword,newPassword) =>{
    return async dispatch => {
        try {
            const response = await axios.post('http://localhost:5000/auth/edit',{
                email,
                oldPassword,
                newPassword
            })
            dispatch(setUser(response.data.user));
            localStorage.setItem('token',response.data.token);//локальное хранилище

            console.log(response.data);
        } catch (e) {
            console.log(e.response);
            // alert(e.response.data.errors.errors[0].msg);

        }
    }

}
