import React, {useState} from 'react';
import {useSelector} from "react-redux";
import {useInput} from "../../utils/validator/validator";
import Input from "../../utils/input/input";
import {edit} from "../../actions/settings";
import {loadFromLocalStorage} from "../../store";

const Settings = () => {
    const email = useSelector(state=>state.user.currentUser.email)
    const top = useSelector(state=>state.app.top)
    const amount = useSelector(state=>state.app.amount)
    const localHistory = loadFromLocalStorage('history');
    const oldPassword = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength: {value:3,text:'min length'},maxLength: {value:8,text:'max length'}})
    const newPassword = useInput('',{isEmpty: {value:true,text:'Field is empty'},minLength: {value:3,text:'min length'},maxLength: {value:8,text:'max length'}})
    const [form,setForm] = useState(false);

    return (
        <div>
        {
            localHistory.length === 0 ?
                <div>
                    You don't play in quiz yet
                </div>
                :
                <div>
                    <img src="" alt=""/>
                    <div className={'d-flex flex-column'}>
                        <div>
                            Email: {email}
                        </div>
                        <div className={'d-flex flex-column'}>
                            <h3>History of the quizzes</h3>
                            <div className={'d-flex'}>
                                {Object.keys(amount).map((item) => {
                                    return <span>{item}: <br/> {amount[item].value}</span>
                                })}
                            </div>
                        </div>
                        <div className={'d-flex flex-column'}>
                            <h3>Top at follow kind</h3>
                            <div className={'d-flex'}>
                                {Object.keys(top).map((item) => {
                                    return <span>{item}: <br/> {top[item]}</span>
                                })}
                            </div>
                        </div>
                        <div className={'d-flex flex-column'}>
                            <h3>AVG of right answer</h3>
                            <div className={'d-flex'}>
                                {Object.keys(amount).map((item) => {
                                    return <span>{item}: <br/> {amount[item].avg}</span>
                                })}
                            </div>
                        </div>
                    </div>
                    <button className={'btn btn-secondary'} onClick={() => setForm(!form)}>Edit profile</button>
                    {form ?
                        <div>
                            {oldPassword.isDirty && oldPassword.printError(['isEmpty', 'minLengthError', 'maxLengthError'])}

                            <Input setValue={oldPassword.onChange} onBlur={oldPassword.onBlur} value={oldPassword.value}
                                   type={"password"} placeholder={"input previous password..."}/>

                            {newPassword.isDirty && newPassword.printError(['isEmpty', 'minLengthError', 'maxLengthError'])}

                            <Input setValue={newPassword.onChange} onBlur={newPassword.onBlur} value={newPassword.value}
                                   type={"password"} placeholder={"input new password..."}/>
                            {/*{(!email.inputValid.value || !password.inputValid.value) && <div>{email.inputValid.text || password.inputValid.text}</div>}*/}

                            <button className={'btn btn-primary'}
                                    disabled={!oldPassword.inputValid.value || !newPassword.inputValid.value}
                                    onClick={() => {
                                        let a = edit(email, oldPassword.value, newPassword.value);
                                        console.log(a);
                                        // console.log(a);
                                        // !a? setForm(false):setForm(true);
                                    }

                                    }>Save
                            </button>
                        </div> : ''
                    }

                </div>
        }
        </div>
    );
};

export default Settings;
