import React, {useEffect,Suspense, lazy} from "react";
import Navbar from "./navbar/Navbar";
import {BrowserRouter, NavLink, Route, Switch} from "react-router-dom";
import "./app.css";
import Registration from "./registration/Registration";
import Login from "./registration/Login";
import {useDispatch, useSelector} from "react-redux";
import {auth} from "../actions/user";
import SelectGame from "./game/SelectGame";
import Game from "./game/Game.jsx";
import History from "./history/History";
import Range from "./range/Range";
import {actionRefreshRange, start} from "../actions/range";
import {startGame} from "../actions/game";
import {store} from "../store";
import Settings from "./settings/Settings";
import {actionRefreshHistory, startHistory} from "../actions/history";


function App() {
    const isAuth = useSelector(state=>state.user.isAuth)
    const isGame = useSelector(state=>state.game.startGame)
    const routes = ['history','biology','geography','mixed'];
    const dispatch = useDispatch();

    actionRefreshHistory();
    actionRefreshRange();

    useEffect(()=>{
        dispatch(auth());
    },[])



    return (
        <BrowserRouter>
            {!isGame ?<div className={"app"}>
                <Navbar />
            </div>
            : ''}
            {!isAuth ?
                <Switch>
                    <Route path={"/registration"} component={Registration}/>
                    <Route path={"/login"} component={Login}/>
                </Switch> :
                <Suspense fallback={<div>Loading...</div>}>
                <Switch>
                    <Route exact path={'/game/preview'}>
                        {routes.map((item) =>  (
                            <NavLink key={item} to={'/game/preview/'+item}>
                                {item}
                            </NavLink>
                        ))}
                    </Route>
                    {routes.map((item)=>(
                        <Route key={item} exact path={'/game/preview/'+item}>
                            {( match ) => ( <div className="page">
                                {routes.map((item) =>  (
                                    <NavLink key={item} to={'/game/preview/'+item}>
                                        {item}
                                    </NavLink>
                                ))}
                                <SelectGame match={match} kind={item}/>
                            </div>)}
                        </Route>
                    ))}



                    <Route exact path={"/game/:name"} component={Game}/>
                    <Route  exact path={"/history"} component={History}/>
                    <Route  exact path={"/range"} component={Range} />
                    <Route exact path={"/settings"} component={Settings}/>
                </Switch>
                </Suspense>
            }

        </BrowserRouter>
);
}

export default App;
